import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from react-router-dom
import './FormPage.css';

const images = [
  'form1.jpg',
  'form2.jpg',
  'form3.jpg',
  // Add more image paths as needed
];

function FormPage() {
  const [currentPage, setCurrentPage] = useState(0);
  const navigate = useNavigate(); // Initialize useNavigate

  const handleBackClick = () => {
    navigate('/'); // Navigate to the home page
  };

  const handleNextClick = () => {
    if (currentPage < images.length - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousClick = () => {
    if (currentPage > 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="form-page">
      <h1>Student Admission Form</h1>
      <img
        src={images[currentPage]}
        alt={`Form Page ${currentPage + 1}`}
        className="form-image" // Use CSS class for styling
      />
      <br />
      <div className="navigation-buttons">
        <button onClick={handlePreviousClick} disabled={currentPage === 0}>
          Previous
        </button>
        <button onClick={handleNextClick} disabled={currentPage === images.length - 1}>
          Next
        </button>
      </div>
      <br />
      <section id="back-to-home-button">
        <button onClick={handleBackClick}>Back to Home</button> {/* Button to navigate back */}
      </section>
    </div>
  );
}

export default FormPage;
