import React, { useState } from 'react';
import './certificate.css';

const ImageCarousel = () => {
    const images = [
        'image1.webp',
        'image2.webp',
        'image3.webp',
        'image4.webp'
    ];

    const [currentIndex, setCurrentIndex] = useState(0);

    const showImage = (index) => {
        return images[index];
    };

    const handlePrevClick = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
    };

    const handleNextClick = () => {
        setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    };

    return (
        <div className="carousel-container-wrapper">
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Image Carousel</title>
        <link rel="stylesheet" href="styles.css" />
        <div className="carousel-container">
            <button id="prevButton" className="arrow" onClick={handlePrevClick}>&#10094;</button>
            <img id="carouselImage" src={showImage(currentIndex)} alt="Carousel" />
            <button id="nextButton" className="arrow" onClick={handleNextClick}>&#10095;</button>
        </div>
    </div>
    );
};

export default ImageCarousel;
