import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './calculator.css';
import FeedbackForm from './FeedbackForm'; // Ensure this is the correct import
import ScientificCalculator from './ScientificCalculator'; // Ensure this is the correct import
import MeasurementConversion from './MeasurementConversion'; // Ensure this is the correct import
import MoneyConversion from './MoneyConversion'; // Ensure this is the correct import

const Calculator = () => {
    const [display, setDisplay] = useState('0');
    const [view, setView] = useState('basic'); // State to manage different views
    const navigate = useNavigate();

    // Basic calculator functions
    const appendNumber = (num) => {
        if (display === '0') {
            setDisplay(num);
        } else {
            setDisplay(display + num);
        }
    };

    const appendOperator = (opt) => {
        setDisplay(display + ' ' + opt + ' ');
    };

    const calculate = () => {
        try {
            setDisplay(eval(display)); // For simplicity; consider using a library for complex calculations
        } catch (error) {
            setDisplay('Error');
        }
    };

    const clearDisplay = () => {
        setDisplay('0');
    };

    const handleBackToHome = () => {
        navigate('/'); // Adjust the route as needed
    };

    const handleViewChange = (viewName) => {
        setView(viewName);
    };

    return (
        <div className="calculator">
            <div className="calculator-label">Calculator</div>

            <div className="calculator-container">
                {view === 'basic' && (
                    <form className="basic-calculator-form">
                        <div className="display" id="display">{display}</div>
                        <div className="button-grid">
                            <button className="btn number" onClick={() => appendNumber('1')}>1</button>
                            <button className="btn number" onClick={() => appendNumber('2')}>2</button>
                            <button className="btn number" onClick={() => appendNumber('3')}>3</button>
                            <button className="btn operator" onClick={() => appendOperator('+')}>+</button>

                            <button className="btn number" onClick={() => appendNumber('4')}>4</button>
                            <button className="btn number" onClick={() => appendNumber('5')}>5</button>
                            <button className="btn number" onClick={() => appendNumber('6')}>6</button>
                            <button className="btn operator" onClick={() => appendOperator('-')}>-</button>

                            <button className="btn number" onClick={() => appendNumber('7')}>7</button>
                            <button className="btn number" onClick={() => appendNumber('8')}>8</button>
                            <button className="btn number" onClick={() => appendNumber('9')}>9</button>
                            <button className="btn operator" onClick={() => appendOperator('*')}>*</button>

                            <button className="btn number" onClick={() => appendNumber('0')}>0</button>
                            <button className="btn operator" onClick={() => appendOperator('/')}>/</button>
                            <button className="btn equal" onClick={calculate}>=</button>
                            <button className="btn clear" onClick={clearDisplay}>C</button>
                        </div>
                    </form>
                )}

                {view === 'scientific' && <ScientificCalculator />}
                {view === 'measurement' && <MeasurementConversion />}
                {view === 'money' && <MoneyConversion />}
                {view === 'feedback' && <FeedbackForm />}
            </div>

            <div className="additional-controls">
                <h2>You may visit</h2>
                <div className="button-controls">
                    <button className="btn extra" onClick={() => handleViewChange('basic')}>Basic Calculator</button>
                    <button className="btn extra" onClick={() => handleViewChange('scientific')}>Scientific Calculator</button>
                    <button className="btn extra" onClick={() => handleViewChange('measurement')}>Measurement Conversion</button>
                    <button className="btn extra" onClick={() => handleViewChange('money')}>Money Conversion</button>
                    <button className="btn extra" onClick={() => handleViewChange('feedback')}>Feedback</button>
                    <button className="btn extra" onClick={handleBackToHome}>Go back</button>
                </div>
            </div>
        </div>
    );
};

export default Calculator;
