import React, { useState, useEffect } from 'react';
import './Todolist.css';

const Todolist = () => {
    const [taskText, setTaskText] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [tasks, setTasks] = useState([]);
    const [completedTasks, setCompletedTasks] = useState([]);

    useEffect(() => {
        // Fetch tasks from backend on component mount
        fetch('/api/tasks')
            .then(response => response.json())
            .then(data => {
                setTasks(data.pendingTasks || []);
                setCompletedTasks(data.completedTasks || []);
            })
            .catch(error => console.error('Error fetching tasks:', error));
    }, []);

    const handleAddTask = () => {
        if (taskText && startTime && endTime) {
            const newTask = {
                id: Date.now(),
                text: taskText,
                startTime: new Date(startTime),
                endTime: new Date(endTime),
                completedAt: null
            };
            setTasks([...tasks, newTask]);
            setTaskText('');
            setStartTime('');
            setEndTime('');
            scheduleNotification(newTask);
            fetch('/api/tasks', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newTask)
            })
            .then(response => response.json())
            .then(data => console.log('Task added:', data))
            .catch(error => console.error('Error adding task:', error));
        }
    };

    const handleCompleteTask = (id) => {
        const taskToComplete = tasks.find(task => task.id === id);
        if (taskToComplete) {
            taskToComplete.completedAt = new Date();
            setTasks(tasks.filter(task => task.id !== id));
            setCompletedTasks([...completedTasks, taskToComplete]);
            
            fetch(`/api/tasks/${id}/complete`, { method: 'PUT' })
                .then(response => response.json())
                .then(data => console.log('Task completed:', data))
                .catch(error => console.error('Error completing task:', error));
        }
    };

    const handleDeleteTask = (id, type) => {
        if (type === 'tasks') {
            setTasks(tasks.filter(task => task.id !== id));
            fetch(`/api/tasks/${id}`, { method: 'DELETE' })
                .then(response => response.json())
                .then(data => console.log('Task deleted:', data))
                .catch(error => console.error('Error deleting task:', error));
        } else {
            setCompletedTasks(completedTasks.filter(task => task.id !== id));
            fetch(`/api/tasks/${id}`, { method: 'DELETE' })
                .then(response => response.json())
                .then(data => console.log('Completed task deleted:', data))
                .catch(error => console.error('Error deleting completed task:', error));
        }
    };

    const scheduleNotification = (task) => {
        const currentTime = new Date();
        const timeDiff = task.endTime.getTime() - currentTime.getTime();
        
        if (timeDiff > 0) {
            setTimeout(() => {
                sendNotification(task);
            }, timeDiff);
        }
    };

    const sendNotification = (task) => {
        fetch('/notify-task', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ task })
        })
        .then(response => response.json())
        .then(data => console.log('Notification sent:', data))
        .catch(error => console.error('Error sending notification:', error));
    };

    return (
        <div className="todolist-container">
            <div className="form-section">
                <h1>Add New Task</h1>
                <div className="form-container">
                    <input
                        type="text"
                        value={taskText}
                        onChange={(e) => setTaskText(e.target.value)}
                        placeholder="Enter task..."
                    />
                    <input
                        type="datetime-local"
                        value={startTime}
                        onChange={(e) => setStartTime(e.target.value)}
                    />
                    <input
                        type="datetime-local"
                        value={endTime}
                        onChange={(e) => setEndTime(e.target.value)}
                    />
                    <button onClick={handleAddTask}>Add Task</button>
                </div>
            </div>
            <div className="lists-section">
                <div className="task-list">
                    <h2>Pending Tasks</h2>
                    <ul className="list">
                        {tasks.map(task => (
                            <li key={task.id}>
                                <span>{task.text}</span>
                                <span className="date-time">
                                    Start Time: {task.startTime.toLocaleDateString()} {task.startTime.toLocaleTimeString()},
                                    End Time: {task.endTime.toLocaleDateString()} {task.endTime.toLocaleTimeString()}
                                </span>
                                <button onClick={() => handleCompleteTask(task.id)}>Complete</button>
                                <button onClick={() => handleDeleteTask(task.id, 'tasks')}>Delete</button>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="completed-list">
                    <h2>Completed Tasks</h2>
                    <ul className="list">
                        {completedTasks.map(task => (
                            <li key={task.id} className="completed">
                                <span>{task.text}</span>
                                <span className="date-time">
                                    Start Time: {task.startTime.toLocaleDateString()} {task.startTime.toLocaleTimeString()},
                                    End Time: {task.endTime.toLocaleDateString()} {task.endTime.toLocaleTimeString()},
                                    Completed At: {task.completedAt ? `${task.completedAt.toLocaleDateString()} ${task.completedAt.toLocaleTimeString()}` : 'Not completed yet'}
                                </span>
                                <button onClick={() => handleDeleteTask(task.id, 'completedTasks')}>Delete</button>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="footer">
                <p align="center">To return click on ðŸ‘‰<a href="/Web">Home</a></p>
            </div>
        </div>
    );
};

export default Todolist;
