import React, { useState, useEffect } from 'react';
import axios from 'axios'; // For making HTTP requests
import './Todolist.css'; // Import your CSS file for styling

const ToDolist = () => {
  const [tasks, setTasks] = useState([]);
  const [taskName, setTaskName] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  useEffect(() => {
    // Fetch tasks from backend on component mount
    axios.get('/api/tasks')
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  const handleAddTask = () => {
    const newTask = { taskName, startTime, endTime, completed: false };
    axios.post('/api/tasks', newTask)
      .then(response => {
        setTasks([...tasks, response.data]);
        // Optionally, you can use a notification library here
      })
      .catch(error => console.error('Error adding task:', error));
  };

  const handleDeleteTask = (id) => {
    axios.delete(`/api/tasks/${id}`)
      .then(() => setTasks(tasks.filter(task => task.id !== id)))
      .catch(error => console.error('Error deleting task:', error));
  };

  const handleCompleteTask = (id) => {
    axios.put(`/api/tasks/${id}`, { completed: true })
      .then(() => {
        setTasks(tasks.map(task => (task.id === id ? { ...task, completed: true } : task)));
        // Optionally, you can use a notification library here
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div className="todo-list">
      <h1>ToDo List</h1>
      <div className="task-form">
        <input
          type="text"
          placeholder="Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
        />
        <input
          type="datetime-local"
          placeholder="Start Time"
          value={startTime}
          onChange={(e) => setStartTime(e.target.value)}
        />
        <input
          type="datetime-local"
          placeholder="End Time"
          value={endTime}
          onChange={(e) => setEndTime(e.target.value)}
        />
        <button onClick={handleAddTask}>Add Task</button>
      </div>
      <div className="tasks">
        <div className="task-list">
          <h2>Remaining Tasks</h2>
          <ul>
            {tasks.filter(task => !task.completed).map(task => (
              <li key={task.id}>
                <span>{task.taskName} (Start: {task.startTime}, End: {task.endTime})</span>
                <button onClick={() => handleCompleteTask(task.id)}>Complete</button>
                <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
        <div className="task-list">
          <h2>Completed Tasks</h2>
          <ul>
            {tasks.filter(task => task.completed).map(task => (
              <li key={task.id}>
                <span>{task.taskName} (Start: {task.startTime}, End: {task.endTime})</span>
                <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ToDoList;
