// src/MoneyConversion.js
import React, { useState } from 'react';
import './calculator.css';
const MoneyConversion = () => {
    const [amount, setAmount] = useState('');
    const [result, setResult] = useState('');

    const handleChange = (e) => {
        setAmount(e.target.value);
    };

    const convertMoney = () => {
        // Basic conversion example (e.g., USD to EUR)
        const value = parseFloat(amount);
        if (!isNaN(value)) {
            setResult((value * 0.85).toFixed(2) + ' EUR'); // Example: USD to EUR
        } else {
            setResult('Invalid Input');
        }
    };

    return (
        <div className="money-conversion">
            <div className="calculator-label">Money Conversion</div>
            <input type="text" value={amount} onChange={handleChange} placeholder="Enter USD" />
            <button className="btn" onClick={convertMoney}>Convert</button>
            <div>Result: {result}</div>
        </div>
    );
};

export default MoneyConversion;
