import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './calculator.css';

const Calculator = () => {
    const [display, setDisplay] = useState('0');
    const [view, setView] = useState('basic'); // State to manage different views
    const navigate = useNavigate();

    // Basic calculator functions
    const appendNumber = (num) => {
        if (display === '0') {
            setDisplay(num);
        } else {
            setDisplay(display + num);
        }
    };

    const appendOperator = (opt) => {
        setDisplay(display + ' ' + opt + ' ');
    };

    const calculate = () => {
        try {
            setDisplay(eval(display)); // For simplicity; consider using a library for complex calculations
        } catch (error) {
            setDisplay('Error');
        }
    };

    const clearDisplay = () => {
        setDisplay('0');
    };

    // Measurement Conversion
    const [measurementInput, setMeasurementInput] = useState('');
    const [measurementResult, setMeasurementResult] = useState('');

    const handleMeasurementChange = (e) => {
        setMeasurementInput(e.target.value);
    };

    const convertMeasurement = () => {
        const value = parseFloat(measurementInput);
        if (!isNaN(value)) {
            setMeasurementResult(value / 1000 + ' km'); // Example: meters to kilometers
        } else {
            setMeasurementResult('Invalid Input');
        }
    };

    // Money Conversion
    const [moneyAmount, setMoneyAmount] = useState('');
    const [moneyResult, setMoneyResult] = useState('');

    const handleMoneyChange = (e) => {
        setMoneyAmount(e.target.value);
    };

    const convertMoney = () => {
        const value = parseFloat(moneyAmount);
        if (!isNaN(value)) {
            setMoneyResult((value * 0.85).toFixed(2) + ' EUR'); // Example: USD to EUR
        } else {
            setMoneyResult('Invalid Input');
        }
    };

    // Feedback
    const [feedback, setFeedback] = useState('');

    const handleFeedbackChange = (e) => {
        setFeedback(e.target.value);
    };

    const submitFeedback = () => {
        alert('Feedback submitted: ' + feedback);
        setFeedback('');
    };

    const handleBackToHome = () => {
        navigate('/'); // Adjust the route as needed
    };

    return (
        <div className="calculator">
            <div className="calculator-label">Calculator</div>

            {view === 'basic' && (
                <>
                    <div className="display" id="display">{display}</div>
                    <button className="btn number" onClick={() => appendNumber('1')}>1</button>
                    <button className="btn number" onClick={() => appendNumber('2')}>2</button>
                    <button className="btn number" onClick={() => appendNumber('3')}>3</button>
                    <button className="btn number" onClick={() => appendOperator('+')}>+</button>

                    <button className="btn number" onClick={() => appendNumber('4')}>4</button>
                    <button className="btn number" onClick={() => appendNumber('5')}>5</button>
                    <button className="btn number" onClick={() => appendNumber('6')}>6</button>
                    <button className="btn number" onClick={() => appendOperator('-')}>-</button>

                    <button className="btn number" onClick={() => appendNumber('7')}>7</button>
                    <button className="btn number" onClick={() => appendNumber('8')}>8</button>
                    <button className="btn number" onClick={() => appendNumber('9')}>9</button>
                    <button className="btn number" onClick={() => appendOperator('*')}>*</button>
                    <button className="btn number" onClick={() => appendNumber('0')}>0</button>
                    <button className="btn number" onClick={() => appendOperator('/')}>/</button>
                    <button className="btn equal" onClick={calculate}>=</button>
                    <button className="btn" onClick={clearDisplay}>C</button>
                </>
            )}

            {view === 'scientific' && (
                <div>
                    {/* Add your scientific calculator logic here */}
                    <div>Scientific Calculator View</div>
                </div>
            )}

            {view === 'measurement' && (
                <div className="measurement-conversion">
                    <div className="calculator-label">Measurement Conversion</div>
                    <input type="text" value={measurementInput} onChange={handleMeasurementChange} placeholder="Enter meters" />
                    <button className="btn" onClick={convertMeasurement}>Convert</button>
                    <div>Result: {measurementResult}</div>
                </div>
            )}

            {view === 'money' && (
                <div className="money-conversion">
                    <div className="calculator-label">Money Conversion</div>
                    <input type="text" value={moneyAmount} onChange={handleMoneyChange} placeholder="Enter USD" />
                    <button className="btn" onClick={convertMoney}>Convert</button>
                    <div>Result: {moneyResult}</div>
                </div>
            )}

            {view === 'feedback' && (
                <div className="feedback">
                    <div className="calculator-label">Feedback</div>
                    <textarea value={feedback} onChange={handleFeedbackChange} placeholder="Enter your feedback here" />
                    <button className="btn" onClick={submitFeedback}>Submit</button>
                </div>
            )}

            <div className="title">
                <button className="btn extra" onClick={() => setView('basic')}>Basic Calculator</button>
                <button className="btn extra" onClick={() => setView('scientific')}>Scientific Calculator</button>
                <button className="btn extra" onClick={() => setView('measurement')}>Measurement Conversion</button>
                <button className="btn extra" onClick={() => setView('money')}>Money Conversion</button>
                <button className="btn extra" onClick={() => setView('feedback')}>Feedback</button>
                <button className="btn back-to-home-button" onClick={handleBackToHome}>Back to Home</button>
            </div>
        </div>
    );
};

export default Calculator;
